select
xx.*,
cult.name as cultidname
from whg_sys_cult cult, whg_goods xx
where cult.id = xx.cultid
and xx.state = 6 and xx.delstate = 0

    and xx.cultid in

        #{item}



    and xx.deptid in

        #{item}





            and xx.etype = #{etype}


            and xx.etype LIKE "%"#{etype}"%"




    and xx.area = #{area}


    and xx.psprovince = #{psprovince}


    and xx.pscity LIKE "%"#{pscity}"%"


    and xx.province = #{province}


    and xx.city = #{city}


    and xx.area = #{area}


    and xx.psprovince = #{psprovince}


    and xx.pscity LIKE "%"#{pscity}"%"


order by xx.statemdfdate desc

