mapper.select:
select xx.*,
cult.name as cultidname
from whg_sys_cult cult, whg_personnel xx
where cult.id = xx.cultid
and xx.state = 6



        AND xx.cultid IN

            #{item}


            and xx.type = #{type}




            and xx.type LIKE "%"#{type}"%"




    AND xx.deptid IN

        #{item}



    and  xx.province = #{province}


    and  xx.city= #{city}


    and  xx.area =#{area}


    and xx.styletype = #{styletype}


    and xx.psprovince = #{psprovince}


    and xx.pscity LIKE "%"#{pscity}"%"


order by xx.statemdfdate desc

select xx.*, cult.name as cultidname from whg_sys_cult cult, whg_personnel xx where cult.id = xx.cultid and xx.state = 6 and xx.delstate = 0 and xx.id != #{id}
AND xx.cultid IN
#{item}
order by xx.statemdfdate desc
